import React from "react";
import { X, Trash2, ChevronDown, ChevronRight, Folder } from "lucide-react";
import { formatFileSize } from "../../utils/mediaUtils";

export default function PlaylistSidebar({
  directories,
  playlist,
  currentIndex,
  isPlaying,
  activeDirectory,
  toggleDirectoryView,
  removeDirectory,
  removeFromPlaylist,
  playMedia,
}) {
  return (
    <div className="w-1/3 bg-gray-800 p-4 overflow-y-auto">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold">Playlist</h2>
        <span className="text-sm text-gray-400">
          {playlist.length} items
        </span>
      </div>

      <div className="mb-4">
        <h3 className="text-sm font-medium text-gray-400 mb-2">
          Directories
        </h3>
        <ul className="space-y-1">
          {directories.map((dir) => (
            <li key={dir.id}>
              <div className="text-sm bg-gray-700 px-3 py-2 rounded flex justify-between items-center cursor-pointer hover:bg-gray-600">
                <div 
                  className="flex items-center flex-1"
                  onClick={() => toggleDirectoryView(dir.id)}
                >
                  {activeDirectory === dir.id ? 
                    <ChevronDown size={16} className="mr-2" /> : 
                    <ChevronRight size={16} className="mr-2" />
                  }
                  <Folder size={16} className="mr-2 text-teal-400" />
                  <span>{dir.name}</span>
                  <span className="ml-2 text-xs text-gray-400">
                    ({dir.items ? dir.items.length : 0} items)
                  </span>
                </div>
                <button
                  onClick={() => removeDirectory(dir.id)}
                  className="text-gray-400 hover:text-white"
                >
                  <X size={16} />
                </button>
              </div>

              {/* Show directory contents if active */}
              {activeDirectory === dir.id && dir.items && (
                <ul className="ml-6 mt-1 space-y-1">
                  {dir.items.map((item, index) => {
                    // Find the index in the main playlist
                    const playlistIndex = playlist.findIndex(p => p.id === item.id);
                    return (
                      <li
                        key={item.id}
                        className={`px-3 py-2 rounded flex justify-between items-center cursor-pointer ${
                          playlistIndex === currentIndex
                            ? "bg-teal-900"
                            : "bg-gray-700 hover:bg-gray-600"
                        }`}
                        onClick={() => playlistIndex >= 0 && playMedia(playlistIndex)}
                      >
                        <div className="flex-1">
                          <div className="flex items-center space-x-2">
                            {playlistIndex === currentIndex && isPlaying && (
                              <div className="w-2 h-2 bg-teal-400 rounded-full"></div>
                            )}
                            <span className="truncate">{item.name}</span>
                          </div>
                          {item.size && (
                            <div className="text-xs text-gray-400 mt-1">
                              {item.type} • {formatFileSize(item.size)}
                            </div>
                          )}
                        </div>
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            removeFromPlaylist(item.id);
                          }}
                          className="text-gray-400 hover:text-white ml-2"
                        >
                          <Trash2 size={16} />
                        </button>
                      </li>
                    );
                  })}
                </ul>
              )}
            </li>
          ))}
          {directories.length === 0 && (
            <li className="text-sm text-gray-500 italic">
              No directories added
            </li>
          )}
        </ul>
      </div>

      <div>
        <h3 className="text-sm font-medium text-gray-400 mb-2">
          All Media Files
        </h3>
        {/* {activeDirectory === null && (
          <ul className="space-y-1">
            {playlist.map((item, index) => (
              <li
                key={item.id}
                className={`px-3 py-2 rounded flex justify-between items-center cursor-pointer ${
                  index === currentIndex
                    ? "bg-teal-900"
                    : "bg-gray-700 hover:bg-gray-600"
                }`}
                onClick={() => playMedia(index)}
              >
                <div className="flex-1">
                  <div className="flex items-center space-x-2">
                    {index === currentIndex && isPlaying && (
                      <div className="w-2 h-2 bg-teal-400 rounded-full"></div>
                    )}
                    <span className="truncate">{item.name}</span>
                  </div>
                  {item.size && (
                    <div className="text-xs text-gray-400 mt-1">
                      {item.type} • {formatFileSize(item.size)}
                    </div>
                  )}
                </div>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    removeFromPlaylist(item.id);
                  }}
                  className="text-gray-400 hover:text-white ml-2"
                >
                  <Trash2 size={16} />
                </button>
              </li>
            ))}
            {playlist.length === 0 && (
              <li className="text-sm text-gray-500 italic">
                Playlist is empty
              </li>
            )}
          </ul>
        )} */}
        {activeDirectory !== null && playlist.length > 0 && (
          <div className="text-sm text-gray-500 italic px-3 py-2">
            Select a directory to view its contents, or close it to view all files.
          </div>
        )}
      </div>
    </div>
  );
}
