import React, { useState } from "react";
import { X, Trash2, ChevronDown, ChevronRight, Folder, Video, Image } from "lucide-react";
import { formatFileSize } from "../../utils/mediaUtils";
import { ArrowUp, ArrowDown } from "lucide-react";

export default function PlaylistSidebar({
  directories,
  playlist,
  currentIndex,
  isPlaying,
  activeDirectory,
  toggleDirectoryView,
  removeDirectory,
  removeFromPlaylist,
  playMedia,
  isDatabasePlaylist
}) {
  // const [searchTerm, setSearchTerm] = useState("");
  const [sortBy, setSortBy] = useState("name");
  const [sortOrder, setSortOrder] = useState("asc");

  // Filter and sort playlist items
  const filteredPlaylist = playlist
    // .filter((item) =>
    //   item.name.toLowerCase().includes(searchTerm.toLowerCase())
    // )
    .sort((a, b) => {
      const order = sortOrder === "asc" ? 1 : -1;
      switch (sortBy) {
        case "name":
          return order * a.name.localeCompare(b.name);
        case "type":
          return order * a.type.localeCompare(b.type);
        case "size":
          return order * (a.size - b.size);
        default:
          return 0;
      }
    });

  return (
    <div className="w-1/3 bg-gray-900 p-4 flex flex-col">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-semibold text-white">
          {isDatabasePlaylist ? "Database Playlist" : "Local Playlist"}
        </h2>
        <div className="flex items-center space-x-2">
          {/* <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="bg-gray-800 text-white px-2 py-1 rounded text-sm"
          /> */}
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="bg-gray-800 text-white px-2 py-1 rounded text-sm"
          >
            <option value="name">Name</option>
            <option value="type">Type</option>
            <option value="size">Size</option>
          </select>
          <button
            onClick={() => setSortOrder(sortOrder === "asc" ? "desc" : "asc")}
            className="p-1 hover:bg-gray-700 rounded"
          >
            {sortOrder === "asc" ? (
              <ArrowUp size={16} />
            ) : (
              <ArrowDown size={16} />
            )}
          </button>
        </div>
      </div>

      {!isDatabasePlaylist && directories.length > 0 && (
        <div className="mb-4">
          <h3 className="text-sm font-medium text-gray-400 mb-2">Directories</h3>
          <div className="space-y-1">
            {directories.map((dir) => (
              <div
                key={dir.path}
                className="flex items-center justify-between bg-gray-800 p-2 rounded"
              >
                <button
                  onClick={() => toggleDirectoryView(dir.path)}
                  className="flex items-center space-x-2 text-white hover:text-teal-400"
                >
                  <Folder size={16} />
                  <span>{dir.name}</span>
                </button>
                <button
                  onClick={() => removeDirectory(dir.path)}
                  className="text-gray-400 hover:text-red-400"
                >
                  <X size={16} />
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="flex-1 overflow-y-auto">
        <div className="space-y-1">
          {filteredPlaylist.map((item, index) => (
            <div
              key={item.path}
              className={`flex items-center justify-between p-2 rounded cursor-pointer ${
                index === currentIndex
                  ? "bg-teal-600"
                  : "bg-gray-800 hover:bg-gray-700"
              }`}
              onClick={() => playMedia(index)}
            >
              <div className="flex items-center space-x-2">
                {item.type === "video" ? (
                  <Video size={16} />
                ) : (
                  <Image size={16} />
                )}
                <span className="text-white">{item.name}</span>
              </div>
              {!isDatabasePlaylist && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    removeFromPlaylist(index);
                  }}
                  className="text-gray-400 hover:text-red-400"
                >
                  <X size={16} />
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
