import React, { useState, useRef, useEffect } from "react";
import { Folder } from "lucide-react";
import { createMediaItem } from "../../utils/mediaUtils";
import { FileBrowser, FileNavbar, FileToolbar, FileList } from "chonky";
import { ChonkyActions, setChonkyDefaults } from 'chonky';
import { ChonkyIconFA } from 'chonky-icon-fontawesome';
import { ref, listAll, getDownloadURL } from 'firebase/storage';
import { storage } from '../../firebase/config';

// Set up Chonky with FontAwesome icons
setChonkyDefaults({ iconComponent: ChonkyIconFA });

// Directory Selection Modal
export default function DirectoryModal({ isOpen, onClose, onAddDirectory, isDatabasePlaylist }) {
  const [directoryName, setDirectoryName] = useState("");
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef(null);
  const [currentFolderPath, setCurrentFolderPath] = useState("/");
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (isDatabasePlaylist && isOpen) {
      loadFirebaseFiles();
    }
  }, [isDatabasePlaylist, isOpen, currentFolderPath]);

  const loadFirebaseFiles = async () => {
    try {
      setLoading(true);
      const storageRef = ref(storage, currentFolderPath);
      const result = await listAll(storageRef);

      const filesPromises = result.items.map(async (item) => {
        const url = await getDownloadURL(item);
        const isVideo = item.name.match(/\.(mp4|webm|ogg)$/i);
        const isImage = item.name.match(/\.(jpg|jpeg|png|gif)$/i);

        return {
          id: item.fullPath,
          name: item.name,
          path: url,
          type: isVideo ? "video" : isImage ? "image" : "unknown",
          isDir: false
        };
      });

      const folderPromises = result.prefixes.map((folder) => ({
        id: folder.fullPath,
        name: folder.name,
        isDir: true
      }));

      const [filesList, foldersList] = await Promise.all([
        Promise.all(filesPromises),
        Promise.all(folderPromises)
      ]);

      setFiles([...foldersList, ...filesList]);
    } catch (error) {
      console.error("Error loading files:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleFileSelect = (e) => {
    if (!isDatabasePlaylist) {
      const files = Array.from(e.target.files);
      setSelectedFiles(files);
    }
  };

  const handleChonkyFileClick = (file) => {
    if (file.isDir) {
      setCurrentFolderPath(file.id);
    } else {
      const selectedFile = {
        id: file.id,
        name: file.name,
        path: file.path,
        type: file.type
      };
      setSelectedFiles([selectedFile]);
    }
  };

  const handleSubmit = async () => {
    if (!directoryName.trim()) {
      alert("Please enter a directory name");
      return;
    }

    if (selectedFiles.length === 0) {
      alert("Please select at least one file");
      return;
    }

    setIsUploading(true);

    try {
      if (isDatabasePlaylist) {
        // For database files, we already have the URLs
        const mediaItems = selectedFiles.map(file => ({
          id: file.id,
          name: file.name,
          path: file.path,
          type: file.type
        }));
        onAddDirectory(directoryName, mediaItems);
      } else {
        // Original local file handling
        const mediaItemPromises = selectedFiles.map(createMediaItem);
        const mediaItems = await Promise.all(mediaItemPromises);
        const validMediaItems = mediaItems.filter(item => item !== null);

        if (validMediaItems.length === 0) {
          alert("No valid media files were uploaded");
          return;
        }

        onAddDirectory(directoryName, validMediaItems);
      }
      onClose();
      setDirectoryName("");
      setSelectedFiles([]);
    } catch (error) {
      console.error("Error creating directory:", error);
      alert("An error occurred while uploading files");
    } finally {
      setIsUploading(false);
    }
  };

  if (!isOpen) return null;

  // Render different content based on playlist type
  const renderContent = () => {
    if (isDatabasePlaylist) {
      return (
        <div className="h-96">
          <FileBrowser
            files={files.map(file => ({
              id: file.id,
              name: file.name,
              isDir: file.isDir,
              thumbnailUrl: file.type === "image" ? file.path : undefined,
              path: file.path
            }))}
            folderChain={[{ id: currentFolderPath, name: currentFolderPath }]}
            onFileClick={({ file }) => handleChonkyFileClick(file)}
            defaultFileViewActionId={ChonkyActions.EnableListView.id}
          >
            <FileNavbar />
            <FileToolbar />
            <FileList />
          </FileBrowser>
        </div>
      );
    }

    return (
      <>
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-400 mb-1">
            Select Media Files
          </label>
          <input
            ref={fileInputRef}
            type="file"
            multiple
            accept="video/*,image/*"
            onChange={handleFileSelect}
            className="hidden"
          />
          <button
            onClick={() => fileInputRef.current?.click()}
            className="w-full flex items-center justify-center space-x-2 px-3 py-2 bg-gray-700 hover:bg-gray-600 rounded border border-gray-600"
          >
            <Folder size={16} />
            <span>Browse Files</span>
          </button>
          {selectedFiles.length > 0 && (
            <div className="mt-2 text-sm text-gray-400">
              {selectedFiles.length} file(s) selected
            </div>
          )}
        </div>
      </>
    );
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4">
      <div className={`bg-gray-800 rounded-lg w-full ${isDatabasePlaylist ? 'max-w-4xl' : 'max-w-md'} p-6`}>
        <h2 className="text-xl font-semibold mb-4">
          {isDatabasePlaylist ? "Select Media from Database" : "Add Media Directory"}
        </h2>

        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-400 mb-1">
            Directory Name
          </label>
          <input
            type="text"
            value={directoryName}
            onChange={(e) => setDirectoryName(e.target.value)}
            placeholder="Enter directory name"
            className="w-full px-3 py-2 bg-gray-700 rounded border border-gray-600 text-white"
          />
        </div>

        {renderContent()}

        <div className="flex justify-end space-x-3 mt-4">
          <button
            onClick={onClose}
            className="px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded"
          >
            Cancel
          </button>
          <button
            onClick={handleSubmit}
            disabled={isUploading || loading}
            className="px-4 py-2 bg-teal-600 hover:bg-teal-700 rounded disabled:opacity-50"
          >
            {isUploading ? "Uploading..." : loading ? "Loading..." : "Add Directory"}
          </button>
        </div>
      </div>
    </div>
  );
}
