import React from "react";
import { X, Trash2 } from "lucide-react";
import { formatFileSize } from "../../utils/mediaUtils";

export default function PlaylistSidebar({
  directories,
  playlist,
  currentIndex,
  isPlaying,
  removeDirectory,
  removeFromPlaylist,
  playMedia,
}) {
  return (
    <div className="w-1/3 bg-gray-800 p-4 overflow-y-auto">
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-xl font-semibold">Playlist</h2>
        <span className="text-sm text-gray-400">
          {playlist.length} items
        </span>
      </div>

      <div className="mb-4">
        <h3 className="text-sm font-medium text-gray-400 mb-2">
          Directories
        </h3>
        <ul className="space-y-1">
          {directories.map((dir) => (
            <li
              key={dir.id}
              className="text-sm bg-gray-700 px-3 py-2 rounded flex justify-between items-center"
            >
              <span>{dir.name}</span>
              <button
                onClick={() => removeDirectory(dir.id)}
                className="text-gray-400 hover:text-white"
              >
                <X size={16} />
              </button>
            </li>
          ))}
          {directories.length === 0 && (
            <li className="text-sm text-gray-500 italic">
              No directories added
            </li>
          )}
        </ul>
      </div>

      <div>
        <h3 className="text-sm font-medium text-gray-400 mb-2">
          Media Files
        </h3>
        <ul className="space-y-1">
          {playlist.map((item, index) => (
            <li
              key={item.id}
              className={`px-3 py-2 rounded flex justify-between items-center ${
                index === currentIndex
                  ? "bg-teal-900"
                  : "bg-gray-700 hover:bg-gray-600"
              }`}
              onClick={() => playMedia(index)}
            >
              <div className="flex-1">
                <div className="flex items-center space-x-2">
                  {index === currentIndex && isPlaying && (
                    <div className="w-2 h-2 bg-teal-400 rounded-full"></div>
                  )}
                  <span className="truncate">{item.name}</span>
                </div>
                {item.size && (
                  <div className="text-xs text-gray-400 mt-1">
                    {item.type} â€¢ {formatFileSize(item.size)}
                  </div>
                )}
              </div>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  removeFromPlaylist(item.id);
                }}
                className="text-gray-400 hover:text-white ml-2"
              >
                <Trash2 size={16} />
              </button>
            </li>
          ))}
          {playlist.length === 0 && (
            <li className="text-sm text-gray-500 italic">
              Playlist is empty
            </li>
          )}
        </ul>
      </div>
    </div>
  );
}
