import React from "react";
import { X, Trash2, ChevronDown, ChevronRight, Folder, Video, Image } from "lucide-react";
import { formatFileSize } from "../../utils/mediaUtils";
import { ArrowUp, ArrowDown } from "lucide-react";

export default function PlaylistSidebar({
  directories,
  playlist,
  currentIndex,
  isPlaying,
  activeDirectory,
  toggleDirectoryView,
  removeDirectory,
  removeFromPlaylist,
  playMedia,
  isDatabasePlaylist
}) {
  // Filter and sort playlist items
  const filteredPlaylist = playlist;

  return (
    <div className="w-1/3 bg-gray-900 p-4 flex flex-col">
      <div className="flex items-center justify-between mb-4">
        <h2 className="text-xl font-semibold text-white">
          {isDatabasePlaylist ? "Database Playlist" : "Local Playlist"}
        </h2>
      </div>

      {/* Show directories section in both modes */}
      {directories.length > 0 && (
        <div className="mb-4">
          <h3 className="text-sm font-medium text-gray-400 mb-2">
            {isDatabasePlaylist ? "Categories" : "Directories"}
          </h3>
          <div className="space-y-1">
            {directories.map((dir) => (
              <div
                key={dir.id}
                className="flex items-center justify-between bg-gray-800 p-2 rounded"
              >
                <button
                  onClick={() => toggleDirectoryView(dir.id)}
                  className="flex items-center space-x-2 text-white hover:text-teal-400"
                >
                  {activeDirectory === dir.id ? (
                    <ChevronDown size={16} />
                  ) : (
                    <ChevronRight size={16} />
                  )}
                  <Folder size={16} />
                  <span>{dir.name}</span>
                </button>
                <button
                  onClick={() => removeDirectory(dir.id)}
                  className="text-gray-400 hover:text-red-400"
                >
                  <X size={16} />
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="flex-1 overflow-y-auto">
        <div className="space-y-1">
          {/* Show items based on active directory */}
          {filteredPlaylist
            .filter(item => !activeDirectory || item.directoryId === activeDirectory)
            .map((item, index) => {
              // Find the actual index in the full playlist
              const fullPlaylistIndex = playlist.findIndex(p => p.id === item.id);
              return (
                <div
                  key={item.id || item.path}
                  className={`flex items-center justify-between p-2 rounded cursor-pointer ${
                    fullPlaylistIndex === currentIndex
                      ? "bg-teal-600"
                      : "bg-gray-800 hover:bg-gray-700"
                  }`}
                  onClick={() => playMedia(fullPlaylistIndex)}
                >
                  <div className="flex items-center space-x-2">
                    {item.type === "video" ? (
                      <Video size={16} />
                    ) : (
                      <Image size={16} />
                    )}
                    <span className="text-white">{item.name}</span>
                  </div>
                  {!isDatabasePlaylist && (
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        removeFromPlaylist(item.id);
                      }}
                      className="text-gray-400 hover:text-red-400"
                    >
                      <X size={16} />
                    </button>
                  )}
                </div>
              );
            })}
        </div>
      </div>
    </div>
  );
}
