import React, { useState, useEffect, useRef } from "react";
import socket, { requestState, joinSession } from "../../services/socketService";

// Player component - minimal UI for displaying media
export default function Player() {
  const [currentItem, setCurrentItem] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);
  const [isCasting, setIsCasting] = useState(false);
  const mediaRef = useRef(null);

  // Check URL for session ID on initial load
  useEffect(() => {
    const searchParams = new URLSearchParams(window.location.search);
    const sessionId = searchParams.get('session');

    console.log("Player detected session ID:", sessionId);

    if (sessionId) {
      joinSession(sessionId);
    } else {
      requestState();
    }
  }, []);

  // Effect to handle socket events from Caster
  useEffect(() => {
    socket.on("casting_state", (data) => {
      console.log("Received casting state:", data);
      setIsCasting(data.isCasting);

      if (data.isCasting) {
        // Update all state if casting is active
        if (data.currentItem) {
          console.log("Setting current item:", data.currentItem);
          setCurrentItem(data.currentItem);
        }
        if (data.isPlaying !== undefined) setIsPlaying(data.isPlaying);
        if (data.volume !== undefined) setVolume(data.volume);
      } else {
        // Reset state if casting is stopped
        setCurrentItem(null);
        setIsPlaying(false);
      }
    });

    socket.on("media_change", (data) => {
      console.log("Media changed:", data);
      setCurrentItem(data.item);
      setIsPlaying(data.isPlaying);
    });

    socket.on("playback_state", (data) => {
      console.log("Playback state changed:", data.isPlaying);
      setIsPlaying(data.isPlaying);
    });

    socket.on("time_update", (data) => {
      // With proper streaming, we can safely do time updates
      // but only when significantly out of sync
      if (mediaRef.current && Math.abs(mediaRef.current.currentTime - data.currentTime) > 5) {
        mediaRef.current.currentTime = data.currentTime;
      }
    });

    socket.on("volume_change", (data) => {
      console.log("Volume changed:", data.volume);
      setVolume(data.volume);
      if (mediaRef.current) {
        mediaRef.current.volume = data.volume;
      }
    });

    return () => {
      socket.off("casting_state");
      socket.off("media_change");
      socket.off("playback_state");
      socket.off("time_update");
      socket.off("volume_change");
    };
  }, []);

  // Effect to sync media playback state
  useEffect(() => {
    if (mediaRef.current && currentItem) {
      if (isPlaying) {
        console.log("Attempting to play video...");
        mediaRef.current.play()
          .then(() => {
            console.log("Playback started successfully");
          })
          .catch((e) => {
            console.error("Playback error:", e);
          });
      } else {
        mediaRef.current.pause();
      }
    }
  }, [isPlaying, currentItem]);

  return (
    <div className="h-screen w-screen bg-black flex flex-col relative">
      <div className="flex-1 flex items-center justify-center overflow-hidden">
        {isCasting ? (
          currentItem && currentItem.type === "video" ? (
            <video
              ref={mediaRef}
              src={currentItem.path}
              className="max-h-full max-w-full"
              preload="auto"
              playsInline
              controls={false}
              onError={(e) => console.error("Video error:", e.target.error)}
            />
          ) : currentItem && currentItem.type === "image" ? (
            <img
              src={currentItem.path}
              alt={currentItem.name}
              className="max-h-full max-w-full object-contain"
            />
          ) : (
            <div className="text-gray-500">Waiting for media...</div>
          )
        ) : (
          <div className="text-center p-8">
            <div className="text-4xl text-gray-500 mb-4">Waiting for Cast</div>
            <p className="text-gray-400">
              The caster needs to press "Start Casting" to begin streaming content
              to this player.
            </p>
          </div>
        )}
      </div>
    </div>
  );
}
