import React, { useState, useRef } from "react";
import { X, Upload, FileVideo, Image, Loader2 } from "lucide-react";
import { createMediaItem } from "../../utils/mediaUtils";

const DirectoryModal = ({ isOpen, onClose, onUpload }) => {
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [error, setError] = useState(null);
  const fileInputRef = useRef(null);

  const handleFileSelect = async (event) => {
    const files = Array.from(event.target.files);
    if (files.length === 0) return;

    setIsUploading(true);
    setError(null);
    setUploadProgress(0);

    try {
      const totalFiles = files.length;
      let uploadedCount = 0;

      for (const file of files) {
        try {
          const mediaItem = await createMediaItem(file);
          onUpload(mediaItem);
          uploadedCount++;
          setUploadProgress((uploadedCount / totalFiles) * 100);
        } catch (err) {
          console.error("Error uploading file:", err);
          setError(`Failed to upload ${file.name}`);
        }
      }

      onClose();
    } catch (err) {
      console.error("Error in upload process:", err);
      setError("Failed to complete upload process");
    } finally {
      setIsUploading(false);
      setUploadProgress(0);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    const files = Array.from(e.dataTransfer.files);
    if (files.length > 0) {
      const input = fileInputRef.current;
      if (input) {
        const dataTransfer = new DataTransfer();
        files.forEach(file => dataTransfer.items.add(file));
        input.files = dataTransfer.files;
        handleFileSelect({ target: { files: input.files } });
      }
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-gray-800 rounded-lg p-6 w-full max-w-2xl mx-4 relative">
        <button
          onClick={onClose}
          className="absolute top-4 right-4 text-gray-400 hover:text-white"
        >
          <X size={24} />
        </button>

        <div className="text-center mb-6">
          <h2 className="text-2xl font-bold text-white mb-2">Upload Media</h2>
          <p className="text-gray-400">
            Drag and drop files or click to select
          </p>
        </div>

        <div
          className={`border-2 border-dashed rounded-lg p-8 text-center transition-colors
            ${isUploading ? 'border-blue-500 bg-blue-500/10' : 'border-gray-600 hover:border-blue-500'}`}
          onDragOver={handleDragOver}
          onDrop={handleDrop}
        >
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleFileSelect}
            multiple
            accept="video/*,image/*"
            className="hidden"
          />

          {isUploading ? (
            <div className="space-y-4">
              <Loader2 className="w-12 h-12 text-blue-500 animate-spin mx-auto" />
              <div className="text-white">Uploading files...</div>
              <div className="w-full bg-gray-700 rounded-full h-2">
                <div
                  className="bg-blue-500 h-2 rounded-full transition-all duration-300"
                  style={{ width: `${uploadProgress}%` }}
                />
              </div>
              <div className="text-gray-400">{Math.round(uploadProgress)}%</div>
            </div>
          ) : (
            <div
              className="cursor-pointer"
              onClick={() => fileInputRef.current?.click()}
            >
              <div className="flex flex-col items-center space-y-4">
                <div className="p-4 bg-blue-500/10 rounded-full">
                  <Upload className="w-8 h-8 text-blue-500" />
                </div>
                <div className="text-white">
                  Click to select files or drag and drop
                </div>
                <div className="text-sm text-gray-400">
                  Supported formats: MP4, WebM, MPEG, MOV, JPG, PNG
                </div>
              </div>
            </div>
          )}
        </div>

        {error && (
          <div className="mt-4 p-4 bg-red-500/10 border border-red-500 rounded-lg">
            <p className="text-red-500 text-center">{error}</p>
          </div>
        )}

        <div className="mt-6 flex justify-center space-x-4">
          <button
            onClick={onClose}
            className="px-4 py-2 text-gray-400 hover:text-white transition-colors"
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default DirectoryModal;
